DROP TABLE IF EXISTS command;
DROP TABLE IF EXISTS usuario;
DROP TABLE IF EXISTS user;

CREATE TABLE command
(
    cmd_id      INT(11)         NOT NULL AUTO_INCREMENT PRIMARY KEY,
    cmd_name    VARCHAR(32)     NOT NULL UNIQUE,
    cmd_desc    TEXT            NULL, 
    cmd_syntax  TEXT            NULL,
    cmd_add_dt  TIMESTAMP       NOT NULL
);

CREATE TABLE user
(
  usr_id          INT(11)         NOT NULL AUTO_INCREMENT PRIMARY KEY,
  usr_nick        VARCHAR(32)     NOT NULL,
  usr_cur_nick    VARCHAR(32)     NOT NULL,   -- Nick atual
  usr_chan        VARCHAR(32)     NOT NULL,   -- Canal

  usr_add_dt      TIMESTAMP NOT NULL,   -- Data/Hora de inclusao do usuario

  usr_join_qtt    INT NULL DEFAULT 0,   -- Qtd de Joins
                                        -- Assim que o bot entra no canal
                                        -- todos usuarios que la estao tem seu join_qtd incrementado
  usr_quit_qtt    INT NULL DEFAULT 0,   -- Qtd de Quits e Parts
                                        -- Se quit_qtd != join_qtd
                                        -- algo esta errado ou o usuario ainda esta no canal.
  usr_msg_qtt     INT NULL DEFAULT 0, -- Qtd de mensagens para o canal
  usr_act_qtt     INT NULL DEFAULT 0, -- Qtd de actions (/me)
  usr_botuse_qtt  INT NULL DEFAULT 0, -- Qtd de vezes que usou o bot (!php ..)
  usr_kick_qtt    INT NULL DEFAULT 0, -- Qtd de vezes chutado do canal
  usr_nickchg_qtt INT NULL DEFAULT 0, -- Qtd de vezes que trocou de nick

  usr_join_dt     TIMESTAMP   NOT NULL,   -- Data/Hora do ultimo Join
  usr_quit_dt     TIMESTAMP   NOT NULL,   -- Data/Hora do ultimo Quit
  usr_max_period  INT(11)     NOT NULL,   -- Maxima Permanencia (Ultimo Quit - Ultimo Join) em Segundos

  usr_msg_dt      TIMESTAMP NULL, -- Data/Hora do envio da ultima mensagem para o canal
  usr_act_dt      TIMESTAMP NULL, -- Data/Hora do ultimo action (/me)
  usr_botuse_dt   TIMESTAMP NULL, -- Data/Hora do ultimo uso do bot (!php ..)
  usr_kick_dt     TIMESTAMP NULL, -- Data/Hora da ultima vez que foi chutado do canal
  usr_nickchg_dt  TIMESTAMP NULL, -- Data/Hora da ultima vez que trocou de nick

  UNIQUE(usr_nick, usr_chan)
);

